INSERT INTO `categories` (`id`, `name`, `image`, `description`, `created_at`, `updated_at`, `slug`) VALUES
(1, 'بحوث', '603cd650355cb.jpg', 'موسوعة علمية تغطي العديد من المجالات نحاول جاهدين من خلالها تقديم اهم وأدق المعلومات.', '2021-01-14 19:28:56', '2021-03-01 10:56:00', 'بحوث'),
(2, 'أراء', '603cd7855627c.jpg', 'نافذة متاحة للتعبير وتبادل الأفكار والآراء، الهواجس والخواطر بحرية في شتى المجالات، المواضيع والقضايا.', '2021-01-14 19:29:24', '2021-03-01 11:01:09', 'أراء'),
(3, 'شخصيات', '603cd85f82f46.jpg', 'أسماء وشخصيات خلّدها التاريخ في مختلف المجالات والازمان القديمة والمعاصرة، لما قدمته للبشرية من إنجازات، إضافات وأفكار نيرة تفرض حضورها الدائم بيننا.', '2021-01-21 19:15:29', '2021-03-01 11:04:47', 'شخصيات'),
(4, 'قضايا المجتمع', '603cd9cde2819.jpg', 'تشمل كل الأمور، المشاكل والقضايا التي تهم المجتمع والظواهر السائدة فيه.', '2021-01-24 20:15:48', '2021-03-01 11:10:53', 'قضايا-المجتمع'),
(5, 'تكنولوجيا', '603cd947ded3b.jpg', 'بوابة تنفتح على عالم التطور التكنولوجي بأهم ابتكاراته وأحدث تقنياته لتضعنا امام عالم رقمي حديث.', '2021-01-25 17:40:37', '2021-03-01 11:08:39', 'تكنولوجيا'),
(6, 'تاريخ', '603cda1f59291.jpg', 'مطرح يعبق برائحة التاريخ المغبرة يأخذنا في رحلة عبر الزمن لنتوقف عند اهم الاحداث التاريخية لنحيي ذكراها من جديد.', '2021-01-25 17:40:57', '2021-03-01 11:12:15', 'تاريخ'),
(7, 'علوم', '603cd8bf4290e.jpg', 'قسم علوم يضع بين يديك كتاب مفتوح تبحر من خلاله بين العلوم متنقلا بين كم هائل من المعلومات في مختلف المجالات.', '2021-01-25 17:41:17', '2021-03-01 11:06:23', 'علوم'),
(8, 'أدب و فنّ', '603cda7206dcc.jpg', 'تسلط الضوء على كل ما يخص الادب والفن في مختلف بقاع العالم من أساليب، تصنيفات واعمال خالدة ومعاصرة.', '2021-01-25 17:42:25', '2021-03-01 11:13:38', 'أدب-و-فن'),
(9, 'معالم و حضارات', '603cd3e8f0acb.jpg', 'تعيد احياء حضارات العالم المتعاقبة على مر العصور ونفض الغبار عن المعالم الاثرية ومختلف اشكال الاثار في كل بقاع الأرض.', '2021-01-30 08:43:35', '2021-03-01 10:45:45', 'معالم-و-حضارات');

