INSERT INTO `categories` (`id`, `name`, `image`, `description`, `created_at`, `updated_at`, `slug`) VALUES
(1, 'بحوث', NULL, 'موسوعة علمية تغطي العديد من المجالات نحاول جاهدين من خلالها تقديم اهم وأدق المعلومات.', '2021-01-14 19:28:56', '2021-02-16 08:36:59', 'بحوث'),
(2, 'أراء', NULL, 'نافذة متاحة للتعبير وتبادل الأفكار والآراء، الهواجس والخواطر بحرية في شتى المجالات، المواضيع والقضايا.', '2021-01-14 19:29:24', '2021-02-16 08:37:03', 'أراء'),
(3, 'شخصيات', NULL, 'أسماء وشخصيات خلّدها التاريخ في مختلف المجالات والازمان القديمة والمعاصرة، لما قدمته للبشرية من إنجازات، إضافات وأفكار نيرة تفرض حضورها الدائم بيننا.', '2021-01-21 19:15:29', '2021-02-16 08:37:07', 'شخصيات'),
(4, 'قضايا المجتمع', NULL, 'تشمل كل الأمور، المشاكل والقضايا التي تهم المجتمع والظواهر السائدة فيه.', '2021-01-24 20:15:48', '2021-02-16 08:36:37', 'قضايا-المجتمع'),
(5, 'تكنولوجيا', NULL, 'بوابة تنفتح على عالم التطور التكنولوجي بأهم ابتكاراته وأحدث تقنياته لتضعنا امام عالم رقمي حديث.', '2021-01-25 17:40:37', '2021-02-16 08:37:12', 'تكنولوجيا'),
(6, 'تاريخ', NULL, 'مطرح يعبق برائحة التاريخ المغبرة يأخذنا في رحلة عبر الزمن لنتوقف عند اهم الاحداث التاريخية لنحيي ذكراها من جديد.', '2021-01-25 17:40:57', '2021-02-16 08:37:17', 'تاريخ'),
(7, 'علوم', NULL, 'قسم علوم يضع بين يديك كتاب مفتوح تبحر من خلاله بين العلوم متنقلا بين كم هائل من المعلومات في مختلف المجالات.', '2021-01-25 17:41:17', '2021-02-16 08:37:49', 'علوم'),
(8, 'أدب و فنّ', NULL, 'تسلط الضوء على كل ما يخص الادب والفن في مختلف بقاع العالم من أساليب، تصنيفات واعمال خالدة ومعاصرة.', '2021-01-25 17:42:25', '2021-02-16 08:37:28', 'أدب-و-فن'),
(9, 'معالم و حضارات', NULL, 'تعيد احياء حضارات العالم المتعاقبة على مر العصور ونفض الغبار عن المعالم الاثرية ومختلف اشكال الاثار في كل بقاع الأرض.', '2021-01-30 08:43:35', '2021-02-16 08:37:34', 'معالم-و-حضارات');

